CREATE OR REPLACE FUNCTION obter_dados_prescr_susp_reapr ( nr_seq_processo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
D - dt_suspensao
R - nm_usuario_reaprazamento
S - nm_usuario_suspensao
*/
nm_usuario_reaprazamento_w  varchar(15);
nm_usuario_susp_w	    varchar(15);
dt_suspensao		    timestamp;
ds_retorno_w		    varchar(255);


BEGIN
IF (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') THEN

	SELECT  MAX(nm_usuario_reaprazamento),
		MAX(nm_usuario_susp),
		MAX(dt_suspensao)
	INTO STRICT	nm_usuario_reaprazamento_w,
		nm_usuario_susp_w,
		dt_suspensao
	FROM    prescr_mat_hor
	WHERE   nr_seq_processo = nr_seq_processo_p;

	IF (ie_opcao_p = 'D') THEN
		ds_retorno_w	:= TO_CHAR(dt_suspensao,'dd/mm/yyyy hh24:mi:ss');
	ELSIF (ie_opcao_p = 'R') THEN
		ds_retorno_w	:= nm_usuario_reaprazamento_w;
	ELSIF (ie_opcao_p = 'S') THEN
		ds_retorno_w	:= nm_usuario_susp_w;
	END IF;
END IF;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
