CREATE OR REPLACE FUNCTION obter_dados_pre_venda_prescr ( nr_prescricao_p bigint, nr_seq_interno_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w  varchar(255);
nr_ordem_venda_w varchar(255);
nr_seq_interno_w bigint;


BEGIN

select  max(nr_seq_interno)
into STRICT  	nr_seq_interno_w
from 	prescr_procedimento b
where  	b.nr_prescricao = nr_prescricao_p
and  	b.nr_sequencia = nr_seq_interno_p;

select 	max(a.nr_ordem_venda)
into STRICT 	nr_ordem_venda_w
from 	pre_venda a,
		pre_venda_item b
where 	a.nr_sequencia = b.nr_seq_pre_venda
and 	b.nr_prescricao = nr_prescricao_p
and 	b.nr_seq_interno = nr_seq_interno_w;

if (ie_opcao_p = 'OV') then
	ds_resultado_w := nr_ordem_venda_w;
end if;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
