CREATE OR REPLACE FUNCTION obter_dados_prior_proced (nr_seq_prioridade_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_dado_w		varchar(255);
ds_prioridade_w		varchar(255);
ds_cor_w		varchar(255);


BEGIN

select	max(ds_prioridade),
	max(ds_cor)
into STRICT	ds_prioridade_w,
	ds_cor_w
from	PROCED_PATOLOGIA_PRIOR
where	nr_sequencia	= nr_seq_prioridade_p;

if (ie_opcao_p = 'P') then
	ds_dado_w	:= ds_prioridade_w;
else
	ds_dado_w	:= ds_cor_w;
end if;

return	ds_dado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
