CREATE OR REPLACE FUNCTION obter_dados_prm ( ie_opcao_p text, dt_inicio_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE

/*
Total de PRM	'T'

*/
ds_retorno_w	varchar(255);


BEGIN

If (ie_opcao_p = 'T') then

	if (coalesce(dt_final_p::text, '') = '') then
		select 	count(*)
		into STRICT	ds_retorno_w
		from	qua_evento_paciente b,
				PRM_FICHA_OCORRENCIA e
		where	e.nr_seq_evento = b.nr_sequencia
		and 	coalesce(b.dt_inativacao::text, '') = ''
		and	trunc(b.dt_evento) = trunc(dt_inicio_p);
	else
		select 	count(*)
		into STRICT	ds_retorno_w
		from	qua_evento_paciente b,
				PRM_FICHA_OCORRENCIA e
		where	e.nr_seq_evento = b.nr_sequencia
		and 	coalesce(b.dt_inativacao::text, '') = ''
		and	trunc(b.dt_evento) between trunc(dt_inicio_p) and trunc(dt_final_p);
	end if;

end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
