CREATE OR REPLACE FUNCTION obter_dados_procaih ( cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_campo_p text) RETURNS varchar AS $body$
DECLARE


qt_idade_minima_w	smallint;
qt_idade_maxima_w	smallint;
qt_permanencia_w	smallint;
ds_retorno_w		varchar(30);


BEGIN
select	a.qt_idade_minima,
	a.qt_idade_maxima,
	a.qt_permanencia
into STRICT	qt_idade_minima_w,
	qt_idade_maxima_w,
	qt_permanencia_w
from	sus_preco_procaih a
where	a.cd_procedimento	= cd_procedimento_p
and	a.ie_origem_proced	= ie_origem_proced_p
and	a.dt_competencia	= (	SELECT	max(x.dt_competencia)
					from	sus_preco_procaih x
					where	x.cd_procedimento	= cd_procedimento_p
					and	x.ie_origem_proced	= ie_origem_proced_p);

if (upper(ie_campo_p) = 'MIN') then
	ds_retorno_w := qt_idade_minima_w;
elsif (upper(ie_campo_p) = 'MAX') then
	ds_retorno_w := qt_idade_maxima_w;
elsif (upper(ie_campo_p) = 'PER') then
	ds_retorno_w := qt_permanencia_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
