SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_proced_prescr ( nr_prescricao_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_procedimento_w	bigint;
ie_exige_laudo_w	varchar(1);
ds_retorno_w		varchar(15);
ie_tipo_convenio_w	smallint;
nr_atendimento_w	bigint;
qt_procedimento_w	double precision;

/* 
Par√¢metro: ie_opcao_p 
1 - cd_procedimento 
2 - qt_procedimento 
*/
 
 
c01 CURSOR FOR 
SELECT	a.cd_procedimento, 
	a.qt_procedimento, 
	b.ie_exige_laudo 
from	prescr_procedimento a, 
	procedimento b 
where	a.nr_prescricao = nr_prescricao_p 
and	a.ie_origem_proced = b.ie_origem_proced 
and	a.cd_procedimento = b.cd_procedimento;


BEGIN 
 
select	max(nr_atendimento) 
into STRICT	nr_atendimento_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
 
if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
 
	select	max(ie_tipo_convenio) 
	into STRICT	ie_tipo_convenio_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_w;
 
	if (ie_tipo_convenio_w = 3) then 
 
		open c01;
		loop 
		fetch c01 into 
			cd_procedimento_w, 
			qt_procedimento_w, 
			ie_exige_laudo_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			if	(ie_exige_laudo_w = 'S' AND ie_opcao_p = 1) then 
				ds_retorno_w := cd_procedimento_w;
			elsif	(ie_exige_laudo_w = 'S' AND ie_opcao_p = 2) then 
				ds_retorno_w := qt_procedimento_w;
			end if;
		end loop;
		close c01;
 
	end if;
 
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_proced_prescr ( nr_prescricao_p bigint, ie_opcao_p bigint) FROM PUBLIC;

