SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_processo_adep ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_origem_processo_w	adep_processo.ie_origem_processo%type	:= null;
ie_status_processo_w		adep_processo.ie_status_processo%type;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	if (ie_opcao_p = 'O') then

		select	max(ie_origem_processo)
		into STRICT	ie_origem_processo_w
		from	adep_processo where		nr_sequencia = nr_sequencia_p LIMIT 1;

		return ie_origem_processo_w;

	elsif (ie_opcao_p = 'D') then

		select	ie_status_processo
		into STRICT	ie_status_processo_w
		from	adep_processo
		where	nr_sequencia = nr_sequencia_p;

		if (ie_status_processo_w = 'D') then
			--return 'Separado';
			return OBTER_DESC_EXPRESSAO(331627);
		elsif (ie_status_processo_w = 'E') then
			--return 'Expedição';
			return OBTER_DESC_EXPRESSAO(321185);
		elsif (ie_status_processo_w = 'G') then
			--return 'Gerado';
			return OBTER_DESC_EXPRESSAO(330985);
		elsif (ie_status_processo_w = 'H') then
			--return 'Higienizado';
			return OBTER_DESC_EXPRESSAO(330999);
		elsif (ie_status_processo_w = 'L') then
			--return 'Conferido';
			return OBTER_DESC_EXPRESSAO(303131);
		elsif (ie_status_processo_w = 'P') then
			--return 'Preparado';
			return OBTER_DESC_EXPRESSAO(321183);
		elsif (ie_status_processo_w = 'R') then
			--return 'Dispensado / Entregue';
			return OBTER_DESC_EXPRESSAO(490559);
		end if;

	end if;

end if;

if (ie_opcao_p <> 'O') then
	return '';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
