SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_processo_compra ( nr_seq_aprovacao_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
ie_tipo_documento_w		varchar(1);
nr_documento_w		bigint;
cd_pessoa_solicitante_w	varchar(10);
nm_pessoa_solicitante_w	varchar(60);
qt_item_pendente_w		bigint;
ie_docto_aprovado_w		varchar(1);
nm_usuario_lib_w		usuario.nm_usuario%type;

/*ie_tipo_p
ND = Numero do documento
CS = Codigo pessoa solicitante
NS = Nome pessoa solicitante
AD = Aprovacao do documento(Retorna 'S',ou 'N' referente a aprovacao do documento
*/
BEGIN

select	obter_tipo_docto_compras_proc(nr_seq_aprovacao_p, '0')
into STRICT	ie_tipo_documento_w
;

if (ie_tipo_documento_w = 'O') then
	begin
	select	max(nr_ordem_compra)
	into STRICT	nr_documento_w
	from	ordem_compra_item
	where	nr_seq_aprovacao = nr_seq_aprovacao_p;

	select	cd_pessoa_solicitante,
		substr(obter_nome_pf_pj(cd_pessoa_solicitante, null),1,60)
	into STRICT	cd_pessoa_solicitante_w,
		nm_pessoa_solicitante_w
	from	ordem_compra
	where	nr_ordem_compra = nr_documento_w;

	select	count(*)
	into STRICT	qt_item_pendente_w
	from	ordem_compra_item
	where (coalesce(coalesce(dt_aprovacao, dt_reprovacao)::text, '') = '')
	and	nr_ordem_compra = nr_documento_w;
	end;
end if;

if (ie_tipo_documento_w = 'S') then
	begin
	select	max(nr_solic_compra)
	into STRICT	nr_documento_w
	from	solic_compra_item
	where	nr_seq_aprovacao = nr_seq_aprovacao_p;

	select	cd_pessoa_solicitante,
		substr(obter_nome_pf_pj(cd_pessoa_solicitante, null),1,60)
	into STRICT	cd_pessoa_solicitante_w,
		nm_pessoa_solicitante_w
	from	solic_compra
	where	nr_solic_compra = nr_documento_w;

	select	count(*)
	into STRICT	qt_item_pendente_w
	from	solic_compra_item
	where (coalesce(coalesce(dt_autorizacao, dt_reprovacao)::text, '') = '')
	and	nr_solic_compra = nr_documento_w;

	end;
end if;

if (ie_tipo_documento_w = 'R') then
	begin
	select	max(nr_requisicao)
	into STRICT	nr_documento_w
	from	item_requisicao_material
	where	nr_seq_aprovacao = nr_seq_aprovacao_p;

	select	cd_pessoa_solicitante,
		substr(obter_nome_pf_pj(cd_pessoa_solicitante, null),1,60)
	into STRICT	cd_pessoa_solicitante_w,
		nm_pessoa_solicitante_w
	from	requisicao_material
	where	nr_requisicao = nr_documento_w;

	select	count(*)
	into STRICT	qt_item_pendente_w
	from	item_requisicao_material
	where (coalesce(coalesce(dt_aprovacao, dt_reprovacao)::text, '') = '')
	and	nr_requisicao = nr_documento_w;

	end;
end if;

if (ie_tipo_documento_w = 'C') then
	begin
	select	max(nr_cot_compra)
	into STRICT	nr_documento_w
	from	cot_compra_item
	where	nr_seq_aprovacao = nr_seq_aprovacao_p;

	select	cd_pessoa_solicitante,
		substr(obter_nome_pf_pj(cd_pessoa_solicitante, null),1,60)
	into STRICT	cd_pessoa_solicitante_w,
		nm_pessoa_solicitante_w
	from	cot_compra
	where	nr_cot_compra = nr_documento_w;

	select	count(*)
	into STRICT	qt_item_pendente_w
	from	cot_compra_item
	where (coalesce(coalesce(dt_aprovacao, dt_reprovacao)::text, '') = '')
	and	nr_cot_compra = nr_documento_w;

	end;
end if;

if (ie_tipo_documento_w = 'G') then
	select	max(nr_sequencia)
	into STRICT	nr_documento_w
	from	contrato
	where	nr_seq_aprovacao = nr_seq_aprovacao_p;

	begin
	select	cd_pessoa_contratante,
		substr(obter_nome_pf_pj(cd_pessoa_contratante, null),1,60),
		nm_usuario_lib
	into STRICT	cd_pessoa_solicitante_w,
		nm_pessoa_solicitante_w,
		nm_usuario_lib_w
	from	contrato
	where	nr_sequencia = nr_documento_w;
	exception
	when no_data_found or too_many_rows then
		cd_pessoa_solicitante_w := null;
		nm_pessoa_solicitante_w := null;
		nm_usuario_lib_w := null;
	end;
	
	if (coalesce(cd_pessoa_solicitante_w::text, '') = '') and (nm_usuario_lib_w IS NOT NULL AND nm_usuario_lib_w::text <> '') then
		begin
		select	cd_pessoa_fisica,
			substr(obter_nome_pf_pj(cd_pessoa_fisica, null),1,60)
		into STRICT	cd_pessoa_solicitante_w,
			nm_pessoa_solicitante_w
		from	usuario
		where	nm_usuario = nm_usuario_lib_w;
		exception
		when no_data_found or too_many_rows then
			cd_pessoa_solicitante_w := null;
			nm_pessoa_solicitante_w := null;
		end;
	end if;

	select	count(1)
	into STRICT	qt_item_pendente_w
	from	contrato
	where	coalesce(dt_aprovacao::text, '') = ''
	and	coalesce(dt_reprovacao::text, '') = ''
	and	nr_sequencia = nr_documento_w;
end if;

if (ie_tipo_documento_w = 'E') then
	select	max(nr_sequencia)
	into STRICT	nr_documento_w
	from	projeto_recurso
	where	nr_seq_aprovacao = nr_seq_aprovacao_p;

	begin
	select	b.cd_pessoa_fisica,
		substr(obter_nome_pf_pj(b.cd_pessoa_fisica, null),1,60)
	into STRICT	cd_pessoa_solicitante_w,
		nm_pessoa_solicitante_w
	from	projeto_recurso a,
		usuario b
	where	a.nm_usuario_lib = b.nm_usuario
	and	a.nr_sequencia = nr_documento_w;
	exception
	when no_data_found or too_many_rows then
		cd_pessoa_solicitante_w := null;
		nm_pessoa_solicitante_w := null;
	end;

	select	count(1)
	into STRICT	qt_item_pendente_w
	from	projeto_recurso
	where	coalesce(dt_aprovacao::text, '') = ''
	and	coalesce(dt_reprovacao::text, '') = ''
	and	nr_sequencia = nr_documento_w;
end if;

if (ie_tipo_p = 'ND') then
	ds_retorno_w	:= nr_documento_w;
elsif (ie_tipo_p = 'CS') then
	ds_retorno_w	:= cd_pessoa_solicitante_w;
elsif (ie_tipo_p = 'NS') then
	ds_retorno_w	:= nm_pessoa_solicitante_w;
elsif (ie_tipo_p = 'AD') then
	ie_docto_aprovado_w	:= 'S';
	if (qt_item_pendente_w > 0) then
		ie_docto_aprovado_w	:= 'N';
	end if;
	ds_retorno_w	:= ie_docto_aprovado_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_processo_compra ( nr_seq_aprovacao_p bigint, ie_tipo_p text) FROM PUBLIC;

