CREATE OR REPLACE FUNCTION obter_dados_processo ( cd_processo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
T - Tipo de processo
*/
ds_retorno_w		varchar(255);
ds_tipo_processo_w	varchar(255);


BEGIN

select	max(coalesce(obter_valor_dominio(137, cd_tipo_processo),wheb_mensagem_pck.get_texto(793287)))
into STRICT	ds_tipo_processo_w
from	processo
where	cd_processo = cd_processo_p;

if (ie_opcao_p = 'T')	then
	ds_retorno_w := ds_tipo_processo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
