CREATE OR REPLACE FUNCTION obter_dados_proc_ditado ( nr_seq_interno_p bigint, ie_informacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
begin
if (nr_seq_interno_p IS NOT NULL AND nr_seq_interno_p::text <> '') then
	begin
	if (upper(ie_informacao_p) = 'U') then
		begin
		select	nm_usuario
		into STRICT	ds_retorno_w
		from	prescr_proc_ditado
		where	nr_seq_prescr_proc = nr_seq_interno_p;
		end;
	elsif (upper(ie_informacao_p) = 'D') then
		begin
		select	to_char(dt_atualizacao,'dd/mm/yyyy hh24:mi:ss')
		into STRICT	ds_retorno_w
		from	prescr_proc_ditado
		where	nr_seq_prescr_proc = nr_seq_interno_p;
		end;

	end if;
    	end;
end if;
exception
when others then
	ds_retorno_w	:=	null;
end;

RETURN	ds_retorno_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
