SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_proc_interno ( nr_seq_proc_interno_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_proced_niss_w			niss_procedimento.ds_proced_niss%type;
cd_tipo_cirurgia_w			proc_interno.cd_tipo_cirurgia%type;
nr_seq_prot_glic_w			proc_interno.nr_seq_prot_glic%type;
ie_tipo_util_w				proc_interno.ie_tipo_util%type;
ie_tipo_w					proc_interno.ie_tipo%type;
ie_exame_gestao_entrega_w	proc_interno.ie_exame_gestao_entrega%type := 'S';
ie_porte_cirurgia_w			proc_interno.ie_porte_cirurgia%type;
ie_finalidade_w				proc_interno.ie_finalidade%type;
ie_sexo_w					proc_interno.ie_sexo%type;
ie_ctrl_glic_w				proc_interno.ie_ctrl_glic%type;


BEGIN
if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then
	select	max(b.ds_proced_niss),
			max(a.cd_tipo_cirurgia),
			max(a.ie_tipo_util),
			max(a.ie_tipo),
			coalesce(max(a.ie_exame_gestao_entrega),'S'),
			max(a.nr_seq_prot_glic),
			max(a.ie_porte_cirurgia),
			max(a.ie_finalidade),
			max(a.ie_sexo),
			coalesce(max(a.ie_ctrl_glic),'NC')
	into STRICT	ds_proced_niss_w,
			cd_tipo_cirurgia_w,
			ie_tipo_util_w,
			ie_tipo_w,
			ie_exame_gestao_entrega_w,
			nr_seq_prot_glic_w,
			ie_porte_cirurgia_w,
			ie_finalidade_w,
			ie_sexo_W,
			ie_ctrl_glic_w
	FROM proc_interno a
LEFT OUTER JOIN niss_procedimento b ON (a.nr_seq_proced_niss = b.nr_sequencia)
WHERE a.nr_sequencia = nr_seq_proc_interno_p;

	if (ie_opcao_p 	= 'PN') then
		return ds_proced_niss_w;
	elsif (ie_opcao_p 	= 'TC') then
		return to_char(cd_tipo_cirurgia_w);
	elsif (ie_opcao_p 	= 'TU') then
		return ie_tipo_util_w;
	elsif (ie_opcao_p	= 'T') 	then
		return ie_tipo_w;
	elsif (ie_opcao_p	= 'G') 	then
		if (ie_ctrl_glic_w <> 'NC') then
			return nr_seq_prot_glic_w;
		end if;
	elsif (ie_opcao_p	= 'GE') then
		return ie_exame_gestao_entrega_w;
	elsif (ie_opcao_p	= 'PO')	then
		return to_char(ie_porte_cirurgia_w);
	elsif (ie_opcao_p	= 'FN')	then
		return to_char(ie_finalidade_w);
	elsif (ie_opcao_p	= 'S')	then
		return ie_sexo_W;
	end if;
end if;

return null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
