CREATE OR REPLACE FUNCTION obter_dados_proc_maior_valor ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
nr_sequencia_w		bigint;
nr_seq_proc_interno_w	bigint;
dt_procedimento_w		timestamp;

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_proc_interno,
		a.dt_procedimento
	from	procedimento_paciente a,
		procedimento b
	where	a.cd_procedimento = b.cd_procedimento
	and	a.ie_origem_proced = b.ie_origem_proced
	and	a.nr_interno_conta = nr_interno_conta_p
	and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
	and	b.ie_classificacao = 1
	order by	vl_procedimento asc;


BEGIN

ds_retorno_w		:= null;
nr_sequencia_w		:= null;
nr_seq_proc_interno_w	:= null;
dt_procedimento_w		:= null;

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	nr_seq_proc_interno_w,
	dt_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	nr_sequencia_w		:= nr_sequencia_w;
	nr_seq_proc_interno_w	:= nr_seq_proc_interno_w;
	dt_procedimento_w		:= dt_procedimento_w;
	end;
end loop;
close c01;

if (ie_opcao_p = 'NR') then
	ds_retorno_w := nr_sequencia_w;
elsif (ie_opcao_p = 'PI') then
	ds_retorno_w := nr_seq_proc_interno_w;
elsif (ie_opcao_p = 'DT') then
	ds_retorno_w := to_char(dt_procedimento_w, 'dd/mm/yyyy hh24:mi:ss');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
