SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_proc_partic (nr_sequencia_p bigint, nr_seq_partic_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
 
 C - Código do médico participante. 
 N - Nome do médico participante. 
 
*/
 
 
ds_retorno_w	varchar(255);
cd_medico_w	varchar(255);
nm_medico_w	varchar(255);


BEGIN 
 
select	max(cd_pessoa_fisica), 
	max(obter_nome_pf_pj(a.cd_pessoa_fisica, null)) 
into STRICT	cd_medico_w, 
	nm_medico_w 
from	procedimento_participante a 
where	a.nr_sequencia	= nr_sequencia_p 
and	a.nr_seq_partic	= nr_seq_partic_p;
 
if (ie_opcao_p	= 'C') then 
	ds_retorno_w	:= cd_medico_w;
elsif (ie_opcao_p	= 'N') then 
	ds_retorno_w	:= nm_medico_w;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
