SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_proc_rot_esp ( ie_tipo_exame_p text, nr_seq_rotina_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
S = Status
O = Origem Proced
P = CÃ³digo Procedimento
I = Proc Interno
Q = Questiona Intervalo
J = Exigir Justificativa
*/
ie_status_inicial_w			varchar(10);
ie_intervalo_w				varchar(1) := 'N';
cd_procedimento_w			bigint 	:= 0;
ie_origem_proced_w			bigint	:= 0;
nr_proc_interno_w			bigint	:= 0;
ds_retorno_w				varchar(20);
ie_tipo_w 					smallint;
ie_ctrl_glic_w 				varchar(5);
nr_seq_prot_glic_w 			bigint;
ie_exige_justificativa_w	varchar(1);


BEGIN

if (ie_tipo_exame_p = 'L') then

	select	CASE WHEN obter_se_exame_lab_checado(max(nr_sequencia))='S' THEN 'C'  ELSE null END ,
			0,
			'NC',
			null,
			coalesce(max(ie_exige_justificativa),'N')
	into STRICT	ie_status_inicial_w,
			ie_tipo_w,
			ie_ctrl_glic_w,
			nr_seq_prot_glic_w,
			ie_exige_justificativa_w
	from    exame_lab_rotina
	where	nr_sequencia = nr_seq_rotina_p;

elsif ( ie_tipo_exame_p = 'P') then

	select	coalesce(max(ie_intervalo),'N'),
			coalesce(max(cd_procedimento),0),
			coalesce(max(ie_origem_proced),0),
			coalesce(max(nr_proc_interno),0),
			1,
			coalesce(SUBSTR(max(obter_ctrl_glic_proc(nr_proc_interno)),1,3),'NC') ie_ctrl_glic,
			coalesce(max(obter_dados_proc_interno(nr_proc_interno,'G')),0) nr_seq_prot_glic,
			coalesce(max(ie_exige_justificativa),'N')
	into STRICT	ie_intervalo_w,
			cd_procedimento_w,
			ie_origem_proced_w,
			nr_proc_interno_w,
			ie_tipo_w,
			ie_ctrl_glic_w,
			nr_seq_prot_glic_w,
			ie_exige_justificativa_w
	from    procedimento_rotina
	where	nr_sequencia = nr_seq_rotina_p;

end if;

if (ie_opcao_p = 'S') then
	ds_retorno_w := ie_status_inicial_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w := cd_procedimento_w;
elsif (ie_opcao_p = 'O') then
	ds_retorno_w := ie_origem_proced_w;
elsif (ie_opcao_p = 'Q') then
	ds_retorno_w := ie_intervalo_w;
elsif (ie_opcao_p = 'I') then
	ds_retorno_w := nr_proc_interno_w;
elsif (ie_opcao_p = 'T') then
	ds_retorno_w := ie_tipo_w;
elsif (ie_opcao_p = 'G') then
	ds_retorno_w := ie_ctrl_glic_w;
elsif (ie_opcao_p = 'N') then
	ds_retorno_w := nr_seq_prot_glic_w;
elsif (ie_opcao_p = 'J') then
	ds_retorno_w := ie_exige_justificativa_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
