CREATE OR REPLACE FUNCTION obter_dados_proj_rat_relatorio (nr_seq_projeto_p bigint, coluna_cobrar_p text, dt_ini_p timestamp, dt_fim_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_hora_cobrar_w	 proj_rat.qt_hora_cobrar%type := 0;
nr_seq_proj_rat_w	 proj_rat.nr_sequencia%type;
ie_status_cobrar_w	 proj_rat.ie_status_cobrar%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_proj_rat_w
from	proj_rat
where	nr_seq_proj = nr_seq_projeto_p
and	dt_liberacao between dt_ini_p and dt_fim_p;

select	ie_status_cobrar
into STRICT	ie_status_cobrar_w
from	proj_rat
where	nr_sequencia = nr_seq_proj_rat_w;

if (ie_status_cobrar_w = 'CV') and (coluna_cobrar_p = 'S') then

	select	qt_hora_cobrar
	into STRICT	qt_hora_cobrar_w
	from	proj_rat
	where	nr_sequencia = nr_seq_proj_rat_w;
elsif (ie_status_cobrar_w = 'NC') and (coluna_cobrar_p = 'N') then
	select	qt_hora_cobrar
	into STRICT	qt_hora_cobrar_w
	from	proj_rat
	where	nr_sequencia = nr_seq_proj_rat_w;
end if;

return	qt_hora_cobrar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
