CREATE OR REPLACE FUNCTION obter_dados_protocolo_conta ( nr_interno_conta_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_seq_doc_convenio_w PROTOCOLO_CONVENIO.NR_SEQ_DOC_CONVENIO%type;


BEGIN
IF (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') THEN

   SELECT   MAX(a.NR_SEQ_DOC_CONVENIO)
   INTO STRICT     nr_seq_doc_convenio_w
   FROM     PROTOCOLO_CONVENIO a,
