SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_protocolo_qtde ( nr_seq_protocolo_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

qt_retorno_w 		bigint := 0;


BEGIN
if (coalesce(nr_seq_protocolo_p,0) > 0) then
	if (ie_opcao_p = 'TR') then
		select 	count(1)
		into STRICT	qt_retorno_w
		from 	titulo_receber
		where 	nr_seq_protocolo = nr_seq_protocolo_p;
	elsif (ie_opcao_p = 'NF') then
		select 	count(1)
		into STRICT	qt_retorno_w
		from 	nota_fiscal
		where 	nr_seq_protocolo = nr_seq_protocolo_p;
	end if;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
