SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_protocolo ( nr_seq_protocolo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* Opção

DT - Data protocolo
DC - Documento Convênio
DD - Data Definitivo
NP - Número do protocolo
DI - Data integração CR
DV - Data vencimento
DE - Data envio
DTP - Data protocolo diltro (DT_MESANO_REF_PAR)
DEN - Data de entrega convenio
DTI - Data inicial protocolo
DTF - Data fibal protocolo
TP - Tipo de protocolo
C -   Convênio
DVNF - Data de vencimento da NF
DTEN - Data de Emissão da Nota
DVT - Data de vencimento do título

*/
dt_mesano_referencia_w	timestamp;
dt_definitivo_w		timestamp;
nr_seq_doc_convenio_w	varchar(20);
nr_protocolo_w		varchar(40);
dt_integracao_cr_w		timestamp;
dt_vencimento_w		timestamp;
dt_envio_w		timestamp;
dt_mesano_ref_par_w	timestamp;
dt_entrega_convenio_w	timestamp;
dt_periodo_final_w	timestamp;
dt_periodo_inicial_w	timestamp;
dt_emissao_nota_w	timestamp;
ie_tipo_protocolo_w	smallint;
cd_convenio_w		integer	:= 0;
nr_seq_nf_saida_w	bigint;


BEGIN

select	dt_mesano_referencia,
	nr_seq_doc_convenio,
	dt_definitivo,
	nr_protocolo,
	dt_integracao_cr,
	dt_vencimento,
	dt_envio,
	dt_mesano_ref_par,
	dt_entrega_convenio,
	dt_periodo_final,
	dt_periodo_inicial,
	ie_tipo_protocolo,
	cd_convenio
into STRICT	dt_mesano_referencia_w,
	nr_seq_doc_convenio_w,
	dt_definitivo_w,
	nr_protocolo_w,
	dt_integracao_cr_w,
	dt_vencimento_w,
	dt_envio_w,
	dt_mesano_ref_par_w,
	dt_entrega_convenio_w,
	dt_periodo_final_w,
	dt_periodo_inicial_w,
	ie_tipo_protocolo_w,
	cd_convenio_w
from	protocolo_convenio
where	nr_seq_protocolo = nr_seq_protocolo_p;

if (ie_opcao_p = 'DT') then
	return	to_char(dt_mesano_referencia_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DTP') then
	return	to_char(dt_mesano_ref_par_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DD') then
	return  to_char(dt_definitivo_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DC') then
	return  nr_seq_doc_convenio_w;
elsif (ie_opcao_p = 'NP') then
	return  nr_protocolo_w;
elsif (ie_opcao_p = 'DI') then
	return to_char(dt_integracao_cr_w,'dd/mm/yyyy hh24:mi:ss');
elsif (ie_opcao_p = 'DV') then
	return to_char(dt_vencimento_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DE') then
	return to_char(dt_envio_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DEN') then
	return	to_char(dt_entrega_convenio_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DTI') then
	return	to_char(dt_periodo_inicial_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DTF') then
	return	to_char(dt_periodo_final_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'TP') then
	return	ie_tipo_protocolo_w;
elsif (ie_opcao_p = 'C') then
	return	cd_convenio_w;
elsif (ie_opcao_p = 'DVNF') then
	select	min(b.dt_vencimento)
	into STRICT	dt_vencimento_w
	from	nota_fiscal_venc b,
		nota_fiscal a
	where	a.nr_seq_protocolo	= nr_seq_protocolo_p
	and	a.nr_sequencia		= b.nr_sequencia;

	return to_char(dt_vencimento_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DTEN') then

	select	max(nr_seq_nf_saida)
	into STRICT	nr_seq_nf_saida_w
	from	titulo_receber
	where	nr_seq_protocolo = nr_seq_protocolo_p
	and  	coalesce(nr_nota_fiscal,'0')	<> '0';

	select	max(dt_emissao)
	into STRICT	dt_emissao_nota_w
	from	nota_fiscal
	where	nr_sequencia = nr_seq_nf_saida_w;

	if (coalesce(dt_emissao_nota_w::text, '') = '') then /*Se não localizou NF vinculada ao título do protocolo, busca direto a NF do protocolo.*/
		select	max(a.dt_emissao)
		into STRICT	dt_emissao_nota_w
		from	nota_fiscal a
		where	a.nr_seq_protocolo	= nr_seq_protocolo_p;

	end if;

	if (coalesce(dt_emissao_nota_w::text, '') = '') then /*Se não localizou NF vinculada ao título do protocolo, busca direto a NF do lote do protocolo.*/
		select	max(a.dt_emissao)
		into STRICT	dt_emissao_nota_w
		from	nota_fiscal a,
			protocolo_convenio b
		where	a.nr_seq_lote_prot	= b.nr_seq_lote_protocolo
		and	b.nr_seq_protocolo	= nr_seq_protocolo_p;

	end if;

	return to_char(dt_emissao_nota_w, 'dd/mm/yyyy');
elsif (ie_opcao_p = 'DVT') then

	select	min(a.dt_vencimento)
	into STRICT	dt_vencimento_w
	from	titulo_receber a
	where	a.nr_seq_protocolo	= nr_seq_protocolo_p
	and	a.ie_situacao		= '1';

	return dt_vencimento_w;

else
	return	null;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_protocolo ( nr_seq_protocolo_p bigint, ie_opcao_p text) FROM PUBLIC;

