SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_psf (cd_item_p bigint, ie_tabela_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nm_item_w	varchar(60);
ds_item_w	varchar(255);
ds_retorno_w	varchar(255);

/*
Tabela: A - Área;
	 M - Micro área;
	 F - Família;

Opção:  C - Código;
	 N - Nome;
	 D - Descrição;
*/
BEGIN
if (cd_item_p IS NOT NULL AND cd_item_p::text <> '') then
	if (coalesce(ie_tabela_p,'A') = 'A') then
		select	ds_area,
			ds_descricao
		into STRICT	nm_item_w,
			ds_item_w
		from	psf_area
		where	ie_situacao	= 'A'
		and	cd_area	= cd_item_p;

	elsif (coalesce(ie_tabela_p,'A') = 'M') then
		select	ds_micro_area,
			ds_descricao
		into STRICT	nm_item_w,
			ds_item_w
		from	psf_micro_area
		where	ie_situacao	= 'A'
		and	cd_micro_area	= cd_item_p;

	elsif (coalesce(ie_tabela_p,'A') = 'F') then
		select	ds_familia,
			ds_descricao
		into STRICT	nm_item_w,
			ds_item_w
		from	psf_familia
		where	ie_situacao	= 'A'
		and	cd_familia	= cd_item_p;
	end if;

	if (coalesce(ie_opcao_p,'N') = 'N') then
		ds_retorno_w	:= nm_item_w;
	elsif (coalesce(ie_opcao_p,'N') = 'D') then
		ds_retorno_w	:= ds_item_w;
	end if;

end if;

RETURN	ds_retorno_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
