SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_qt_gestao_popul ( cd_pessoa_fisica_p text, ie_opcao_p text, dt_inicio_p timestamp default null, dt_fim_p timestamp default null, cd_profissional_p text default null, nr_seq_servico_p bigint default null) RETURNS bigint AS $body$
DECLARE

 
/*Descrição 			ie_opcao_p 
Qtd consultas			QC 
Qtd falta consultas		QF 
*/
 
 
qt_consultas_agend_w		bigint := 0;
qt_consultas_realizada_w	bigint := 0;
qt_retorno_w				bigint := 0;


BEGIN 
 
if (ie_opcao_p = 'QC') then 
 
	Select 	count(1) 
	into STRICT	qt_retorno_w 
	from  	agenda_consulta a, 
			atendimento_paciente b 
	where 	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and  	(a.nr_atendimento IS NOT NULL AND a.nr_atendimento::text <> '') 
	and		b.ie_nivel_atencao = 'P' 
	and  	a.nr_atendimento = b.nr_atendimento 
	and		coalesce(b.cd_medico_resp,0) = coalesce(cd_profissional_p,coalesce(b.cd_medico_resp,0)) 
	and		((coalesce(nr_seq_servico_p::text, '') = '') or (obter_se_pac_possui_servico(b.nr_atendimento, nr_seq_servico_p) = 'S')) 
	and  	b.ie_nivel_atencao = 'P' 
	and		trunc(a.dt_consulta) between coalesce(dt_inicio_p,trunc(a.dt_consulta)) and coalesce(dt_fim_p,clock_timestamp());
 
elsif (ie_opcao_p = 'QF') then 
 
	Select 	count(1) 
	into STRICT	qt_consultas_agend_w 
	from  	agenda_consulta a, 
			atendimento_paciente b 
	where 	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and  	(a.nr_atendimento IS NOT NULL AND a.nr_atendimento::text <> '') 
	and		b.ie_nivel_atencao = 'P' 
	and  	a.nr_atendimento = b.nr_atendimento 
	and		coalesce(b.cd_medico_resp,0) = coalesce(cd_profissional_p,coalesce(b.cd_medico_resp,0)) 
	and		((coalesce(nr_seq_servico_p::text, '') = '') or (obter_se_pac_possui_servico(b.nr_atendimento, nr_seq_servico_p) = 'S')) 
	and  	b.ie_nivel_atencao = 'P' 
	and		trunc(a.dt_consulta) between coalesce(dt_inicio_p,trunc(a.dt_consulta)) and coalesce(dt_fim_p,clock_timestamp());
	 
	Select 	count(1) 
	into STRICT	qt_consultas_realizada_w 
	from  	agenda_consulta a, 
			atendimento_paciente b 
	where 	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and		b.ie_nivel_atencao = 'P' 
	and  	(a.nr_atendimento IS NOT NULL AND a.nr_atendimento::text <> '') 
	and  	a.nr_atendimento = b.nr_atendimento 
	and		exists (SELECT 1 from atendimento_soap c where c.nr_atendimento = b.nr_Atendimento) 
	and		coalesce(b.cd_medico_resp,0) = coalesce(cd_profissional_p,coalesce(b.cd_medico_resp,0)) 
	and		((coalesce(nr_seq_servico_p::text, '') = '') or (obter_se_pac_possui_servico(b.nr_atendimento, nr_seq_servico_p) = 'S')) 
	and  	b.ie_nivel_atencao = 'P' 
	and		trunc(a.dt_consulta) between coalesce(dt_inicio_p,trunc(a.dt_consulta)) and coalesce(dt_fim_p,clock_timestamp());
	 
	qt_retorno_w := qt_consultas_agend_w - qt_consultas_realizada_w;
	 
end if;
 
return qt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
