SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_qua_doc_local ( nr_seq_doc_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


ds_setor_w		varchar(80);
ds_localizacao_eletronica_w	varchar(255);
ds_retorno_w		varchar(255);


BEGIN
/*
NS = Nome do setor de atendimento
DL = Descrição da localização eletrônica
*/
select	max(substr(obter_nome_setor(cd_setor_atendimento),1,80))
into STRICT	ds_setor_w
from	qua_doc_local
where	nr_seq_doc = nr_seq_doc_p
and	ie_forma_armazenamento <> 'E';

select	max(ds_localizacao_eletronica)
into STRICT	ds_localizacao_eletronica_w
from	qua_doc_local
where	nr_seq_doc = nr_seq_doc_p
and	ie_forma_armazenamento <> 'F';

if (ie_retorno_p = 'NS') then
	ds_retorno_w := ds_setor_w;
elsif (ie_retorno_p = 'DL') then
	ds_retorno_w := ds_localizacao_eletronica_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_qua_doc_local ( nr_seq_doc_p bigint, ie_retorno_p text) FROM PUBLIC;

