SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_registro_cobranca ( nr_seq_reg_cobr_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
nm_operador_w			varchar(255);
nr_seq_atend_evento_w		bigint;
nr_seq_atendimento_w		bigint;
nr_seq_operador_w		bigint;

/* opcao 
 - NO - Nome operador 
 */
 
 

BEGIN 
 
if (ie_opcao_p = 'NO') then 
	select	max(nr_seq_atend_evento) 
	into STRICT	nr_seq_atend_evento_w 
	from	registro_cobranca 
	where	nr_sequencia	= nr_seq_reg_cobr_p;
 
	if (nr_seq_atend_evento_w IS NOT NULL AND nr_seq_atend_evento_w::text <> '') then 
		select	max(nr_seq_atendimento) 
		into STRICT	nr_seq_atendimento_w 
		from	pls_atendimento_evento 
		where	nr_sequencia	= nr_seq_atend_evento_w;
		 
		if (nr_seq_atendimento_w IS NOT NULL AND nr_seq_atendimento_w::text <> '') then 
			select	max(nr_seq_operador) 
			into STRICT	nr_seq_operador_w 
			from	pls_atendimento 
			where	nr_sequencia	= nr_seq_atendimento_w;
			 
			if (nr_seq_atendimento_w IS NOT NULL AND nr_seq_atendimento_w::text <> '') then 
				select	substr(obter_nome_pf(pf.cd_pessoa_fisica),1,255) 
				into STRICT	nm_operador_w 
				from	pessoa_fisica	pf, 
					pls_operador	a 
				where	a.cd_pessoa_fisica	= pf.cd_pessoa_fisica 
				and	a.nr_sequencia		= nr_seq_operador_w;
			end if;
		end if;
	end if;
	/*select	e.nm_pessoa_fisica 
	into	nm_operador_w 
	from	registro_cobranca a, 
		pls_atendimento_evento b, 
		pls_atendimento c, 
		pls_operador d, 
		pessoa_fisica e 
	where	a.nr_sequencia	= nr_seq_reg_cobr_p 
	and	a.nr_seq_atend_evento = b.nr_sequencia 
	and	c.nr_sequencia = b.nr_seq_atendimento 
	and	d.nr_sequencia = c.nr_seq_operador 
	and	e.cd_pessoa_fisica = d.cd_pessoa_fisica;*/
 
end if;
 
return	nm_operador_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_registro_cobranca ( nr_seq_reg_cobr_p bigint, ie_opcao_p text) FROM PUBLIC;

