SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_registro_kit ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_pedido_agenda_w	bigint;
nr_seq_agenda_w		bigint;
nm_paciente_w		varchar(255);
nr_cirurgia_w		bigint;

ds_retorno_w		varchar(255);
/* opcao
 - N - nome paciente
 - CR - Cirurgia origem
 - D - Descrição
 */
BEGIN
if (ie_opcao_p = 'N') then
	begin
	select	nr_seq_pedido_agenda
	into STRICT 	nr_seq_pedido_agenda_w
	from 	kit_estoque_reg
	where 	nr_sequencia = nr_sequencia_p;

	select	nr_seq_agenda
	into STRICT	nr_seq_agenda_w
	from   	agenda_pac_pedido
	where 	nr_sequencia = nr_seq_pedido_agenda_w;

	select	nm_paciente
	into STRICT	nm_paciente_w
	from 	agenda_paciente
	where 	nr_sequencia = nr_seq_agenda_w;

	ds_retorno_w := nm_paciente_w;
	end;
elsif (ie_opcao_p = 'CR') then
	begin
	select	max((obter_dados_kit_estoque(nr_seq_kit_origem, 'CR'))::numeric )
	into STRICT	nr_cirurgia_w
	from	kit_estoque
	where	nr_seq_reg_kit = nr_sequencia_p;

	ds_retorno_w := to_char(nr_cirurgia_w);
	end;
elsif (ie_opcao_p = 'D') then
	begin
	select	substr(DS_REGISTRO_KIT,1,255)
	into STRICT	ds_retorno_w
	from	kit_estoque_reg
	where	nr_sequencia = nr_sequencia_p;
	exception
	when others then
		ds_retorno_w	:=	null;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
