CREATE OR REPLACE FUNCTION obter_dados_regra_ajuste (nr_sequencia_p bigint, ie_proc_mat_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);

/*
Criei as opções para que futuramente possa ser utilizado a mesma function

ie_proc_mat_p
	p - Procedimento
	M - Material

ie_opcao_p
	O - Observação (DS_OBSERVACAO)
*/
BEGIN

if (ie_proc_mat_p = 'P') then

	if (ie_opcao_p = 'O') then

		select 	max(ds_observacao)
		into STRICT	ds_retorno_w
		from 	regra_ajuste_proc
		where 	nr_sequencia = nr_sequencia_p;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
