CREATE OR REPLACE FUNCTION obter_dados_regra_atrib_deriv ( nr_seq_derivado_p bigint, cd_setor_atendimento_p bigint, ie_opcao_p text, qt_peso_p bigint) RETURNS varchar AS $body$
DECLARE


qt_peso_max_w		double precision;
ie_filtrado_w		varchar(1);
ie_irradiado_w		varchar(1);
ds_observacao_w		varchar(4000);
ds_retorno_w		varchar(4000);
ie_apres_mensagem_w	varchar(1);

/*
   P - Peso
   F - Filtrado
   O - Observação
   I  - Irradiado
*/
BEGIN

if (nr_seq_derivado_p IS NOT NULL AND nr_seq_derivado_p::text <> '') then
	select	max(qt_peso_max),
		max(ie_filtrado),
		max(ds_observacao),
		max(ie_irradiado),
		coalesce(max(ie_apres_mensagem),'N')
	into STRICT	qt_peso_max_w,
		ie_filtrado_w,
		ds_observacao_w,
		ie_irradiado_w,
		ie_apres_mensagem_w
	from	rep_definicao_atrib_hem
	where	nr_seq_derivado = nr_seq_derivado_p
	and	((coalesce(cd_setor_atendimento::text, '') = '') or (cd_setor_atendimento = cd_setor_atendimento_p))
	and	((coalesce(qt_peso_max::text, '') = '') or (qt_peso_max >= qt_peso_p));

	if (ie_opcao_p = 'F') then
		ds_retorno_w := ie_filtrado_w;
	elsif (ie_opcao_p = 'O') then
		ds_retorno_w := ds_observacao_w;
	elsif (ie_opcao_p = 'M') then
		ds_retorno_w := ie_apres_mensagem_w;
	elsif (ie_opcao_p = 'I') then
		ds_retorno_w := ie_irradiado_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
