SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_regra_contato_pj ( cd_cnpj_p text, cd_material_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w		bigint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;
ds_email_w			varchar(255);
ds_contato_w			varchar(255);
ds_retorno_w			varchar(255);
cd_perfil_w			bigint;

c01 CURSOR FOR
SELECT	a.ds_email,
	a.ds_contato
from	pessoa_juridica_cont_estr a
where	cd_cnpj = cd_cnpj_p
and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
and (cd_perfil = cd_perfil_w or coalesce(cd_perfil::text, '') = '')
order by
	coalesce(cd_perfil,99999) desc,
	coalesce(cd_material,0),
	coalesce(cd_classe_material,0),
	coalesce(cd_subgrupo_material,0),
	coalesce(cd_grupo_material,0);


BEGIN

cd_perfil_w := Obter_Perfil_Ativo;

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	ds_email_w,
	ds_contato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_email_w		:= ds_email_w;
	ds_contato_w		:= ds_contato_w;
	end;
end loop;
close C01;

if (ie_retorno_p = 'E') then
	ds_retorno_w := ds_email_w;
elsif (ie_retorno_p = 'C') then
	ds_retorno_w := ds_contato_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
