CREATE OR REPLACE FUNCTION obter_dados_regra_hab_deriv ( nr_seq_derivado_p bigint, cd_setor_atendimento_p bigint, ie_opcao_p text, qt_peso_p bigint) RETURNS varchar AS $body$
DECLARE


ie_aliquotado_w		varchar(1);
ds_retorno_w		varchar(4000);

/*
   A - Aliquotado
*/
BEGIN

if (nr_seq_derivado_p IS NOT NULL AND nr_seq_derivado_p::text <> '') then
	select	max(ie_permite_aliquotado)
	into STRICT	ie_aliquotado_w
	from	rep_definicao_hab_hem
	where	nr_seq_derivado = nr_seq_derivado_p
	and	((coalesce(cd_setor_atendimento::text, '') = '') or (cd_setor_atendimento = cd_setor_atendimento_p))
	and	((coalesce(qt_peso_min::text, '') = '' and coalesce(qt_peso_max::text, '') = '') or (qt_peso_p between coalesce(qt_peso_min,0) and coalesce(qt_peso_max,999)));

	if (ie_opcao_p = 'A') then
		ds_retorno_w := coalesce(ie_aliquotado_w,'S');
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
