SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_regra_inter_medic ( nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_cor_inte_w   	varchar(50);
ie_severidade_w		varchar(50);
ds_fonte_w			varchar(255);
ds_retorno_w		varchar(255);
qt_tamanho_w		smallint;


BEGIN

select	max(ie_severidade)
into STRICT	ie_severidade_w
from	prescr_medica_interacao
where	nr_prescricao	= nr_prescricao_p;

if (ie_opcao_p = 'C') then
	select	max(ds_cor)
	into STRICT	ds_cor_inte_w
	from	rep_cor_interacao
	where	ie_severidade	= ie_severidade_w;

	ds_retorno_w := ds_cor_inte_w;
elsif (ie_opcao_p = 'T') then
	select	max(qt_tamanho)
	into STRICT	qt_tamanho_w
	from	rep_cor_interacao
	where	ie_severidade	= ie_severidade_w;

	ds_retorno_w := to_char(qt_tamanho_w);
elsif (ie_opcao_p = 'F') then
	select	max(ds_fonte)
	into STRICT	ds_fonte_w
	from	rep_cor_interacao
	where	ie_severidade	= ie_severidade_w;

	ds_retorno_w := ds_fonte_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_regra_inter_medic ( nr_prescricao_p bigint, ie_opcao_p text) FROM PUBLIC;

