SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_regra_result_sae ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
Resultado do item		R
Item a examinar			I
Evento					E
				*/
ds_resultado_w	varchar(255);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	if (ie_opcao_p = 'E') then

	Select 	substr(ds_evento,1,255)
	into STRICT	ds_resultado_w
	from	qua_evento
	where	nr_sequencia = nr_sequencia_p;

	elsif (ie_opcao_p = 'R') then

	Select 	substr(ds_resultado,1,255)
	into STRICT	ds_resultado_w
	from	pe_item_resultado
	where	nr_sequencia = nr_sequencia_p;

	elsif (ie_opcao_p = 'I') then

	Select 	substr(ds_item,1,255)
	into STRICT	ds_resultado_w
	from	pe_item_examinar
	where	nr_sequencia = nr_sequencia_p;

	end if;

end if;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
