SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_reg_cobr_item ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

					 
/* IE_OPCAO_P 
TI - Tipo 
T - Número título 
C - Número do cheque 
VE - Vencimento 
VO - Valor original 
VC - Valor a cobrar 
DM - Data da mensalidade 
*/
 
					 
ds_retorno_w		varchar(255);
nr_titulo_w		bigint	:= null;
nr_seq_cheque_w		bigint	:= null;
nr_cheque_w		varchar(30)	:= null;
dt_mensalidade_w		varchar(30)	:= null;
dt_vencimento_w		timestamp;
vl_original_w		double precision;
vl_acobrar_w		double precision;


BEGIN 
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	select	b.nr_titulo, 
		b.nr_seq_cheque, 
		b.vl_acobrar 
	into STRICT	nr_titulo_w, 
		nr_seq_cheque_w, 
		vl_acobrar_w 
	from	cobranca b, 
		registro_cobr_item a 
	where	a.nr_sequencia		= nr_sequencia_p 
	and	a.nr_seq_cobranca	= b.nr_sequencia;
	 
	if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then 
		select	a.dt_pagamento_previsto, 
			(obter_dados_titulo_receber(a.nr_titulo,'V'))::numeric , 
			pls_obter_dados_mensalidade(a.nr_seq_mensalidade,'D') 
		into STRICT	dt_vencimento_w, 
			vl_original_w, 
			dt_mensalidade_w 
		from	titulo_receber a 
		where	a.nr_titulo	= nr_titulo_w;
	elsif (nr_seq_cheque_w IS NOT NULL AND nr_seq_cheque_w::text <> '') then 
		select	a.dt_vencimento_atual, 
			a.vl_cheque, 
			a.nr_cheque 
		into STRICT	dt_vencimento_w, 
			vl_original_w, 
			nr_cheque_w 
		from	cheque_cr a 
		where	a.nr_seq_cheque	= nr_seq_cheque_w;
	end if;
 
	if (ie_opcao_p = 'TI') then 
		if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then 
			ds_retorno_w	:= Wheb_mensagem_pck.get_texto(303534);
		else 
			ds_retorno_w	:= Wheb_mensagem_pck.get_texto(303535);
		end if;
	elsif (ie_opcao_p = 'T') then 
		ds_retorno_w	:= nr_titulo_w;
	elsif (ie_opcao_p = 'C') then 
		ds_retorno_w	:= nr_cheque_w;
	elsif (ie_opcao_p = 'VE') then 
		ds_retorno_w	:= dt_vencimento_w;
	elsif (ie_opcao_p = 'VO') then 
		ds_retorno_w	:= vl_original_w;
	elsif (ie_opcao_p = 'VC') then 
		ds_retorno_w	:= vl_acobrar_w;
	elsif (ie_opcao_p = 'DM') then 
		ds_retorno_w	:= dt_mensalidade_w;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
