SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_reg_entrega_hist ( nr_seq_laudo_p bigint, nr_seq_result_lab_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
D = dt_atualizacao
H = dt_atualizacao
N = nm_usuario
*/
nr_sequencia_w	bigint;
ds_retorno_w	varchar(255);


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	registro_entrega_hist
where (nr_seq_laudo = nr_seq_laudo_p) or (nr_seq_result_lab = nr_seq_result_lab_p)
and	ie_etapa_hist = 'EC';


if (ie_opcao_p = 'D') then

	select	max(to_char(dt_atualizacao,'dd/mm/yyyy'))
	into STRICT	ds_retorno_w
	from	registro_entrega_hist
	where	nr_sequencia = nr_sequencia_w;

elsif (ie_opcao_p = 'H') then

	select	max(to_char(dt_atualizacao,'hh24:mi:ss'))
	into STRICT	ds_retorno_w
	from	registro_entrega_hist
	where	nr_sequencia = nr_sequencia_w;

elsif (ie_opcao_p = 'N') then

	select	max(nm_usuario)
	into STRICT	ds_retorno_w
	from	registro_entrega_hist
	where	nr_sequencia = nr_sequencia_w;


end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
