CREATE OR REPLACE FUNCTION obter_dados_relat ( nr_seq_relatorio_p bigint, nr_seq_banda_p bigint, nr_seq_campo_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_resultado_w	varchar(255);

/* 
ie_opcao 
1 = código 
2 = desc. banda 
3 = desc. label 
4 = desc. atributo 
5 = desc. campo 
6 = Classif relatório 
*/
 
 

BEGIN 
 
if (coalesce(ie_opcao_p,0) = 1) then 
 
	select	cd_relatorio 
	into STRICT	ds_resultado_w 
	from	relatorio 
	where	nr_sequencia = nr_seq_relatorio_p;
 
elsif (coalesce(ie_opcao_p,0) = 2) then 
 
	select	ds_banda 
	into STRICT	ds_resultado_w 
	from	banda_relatorio 
	where	nr_sequencia = nr_seq_banda_p;
 
elsif (coalesce(ie_opcao_p,0) = 3) then 
 
	select	ds_label 
	into STRICT	ds_resultado_w 
	from	banda_relat_campo 
	where	nr_sequencia = nr_seq_campo_p;
 
elsif (coalesce(ie_opcao_p,0) = 4) then 
 
	select	nm_atributo 
	into STRICT	ds_resultado_w 
	from	banda_relat_campo 
	where	nr_sequencia = nr_seq_campo_p;
 
elsif (coalesce(ie_opcao_p,0) = 5) then 
 
	select	ds_campo 
	into STRICT	ds_resultado_w 
	from	banda_relat_campo 
	where	nr_sequencia = nr_seq_campo_p;
 
elsif (coalesce(ie_opcao_p,0) = 6) then 
 
	select	cd_classif_relat 
	into STRICT	ds_resultado_w 
	from	relatorio 
	where	nr_sequencia = nr_seq_relatorio_p;
 
end if;
 
return ds_resultado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
