CREATE OR REPLACE FUNCTION obter_dados_rel_iasep ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		varchar(255);

/*
ie_opcao_p
VME- Valor dos medicamentos
VMA - Valor dos materiais
*/
BEGIN
if (ie_opcao_p = 'VME') then
	select	sum(vl_material) vl_medicamentos
	into STRICT	ds_retorno_w
	from 	material_atend_paciente
	where	nr_interno_conta = nr_interno_conta_p
	and 	obter_classif_material_proced(cd_material, null, null) in (2,3,4,8,9);
elsif (ie_opcao_p = 'VMA') then
	select	sum(vl_material) vl_materiais
	into STRICT	ds_retorno_w
	from 	material_atend_paciente
	where 	nr_interno_conta = nr_interno_conta_p
	and 	obter_classif_material_proced(cd_material, null, null) in (1,5,10);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
