SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_repasse_terc ( nr_repasse_terceiro_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* ie_opcao_p 
	C - CGC ou CPF do terceiro 
	N - Nome do terceiro 
	CV - Convenio descricao 
	ST - Status 
	DT - Mês referência 
*/
 
 
nr_seq_terceiro_w	bigint;
cd_convenio_w		integer;
ds_convenio_w		varchar(255);
ie_status_w		varchar(2);
cd_cgc_cpf_w		varchar(20);
nm_terceiro_w		varchar(255);
dt_mesano_referencia_w	varchar(255);


BEGIN 
 
select	nr_seq_terceiro, 
	ie_status, 
	cd_convenio, 
	to_char(dt_mesano_referencia) 
into STRICT	nr_seq_terceiro_w, 
	ie_status_w, 
	cd_convenio_w, 
	dt_mesano_referencia_w 
from	repasse_terceiro 
where	nr_repasse_terceiro = nr_repasse_terceiro_p;
 
select	coalesce(a.cd_cgc, Elimina_Caracteres_Especiais(obter_dados_pf(a.cd_pessoa_fisica,'CPF'))), 
	substr(obter_nome_terceiro(nr_sequencia),1,80) 
into STRICT	cd_cgc_cpf_w, 
	nm_terceiro_w 
from	terceiro a 
where	nr_sequencia	= nr_seq_terceiro_w;
 
if (ie_opcao_p = 'C') then 
	return cd_cgc_cpf_w;
elsif (ie_opcao_p = 'N') then 
	return nm_terceiro_w;
elsif (ie_opcao_p = 'CV') then 
	ds_convenio_w	:= SUBSTR(obter_nome_convenio(cd_convenio_w),1,200);
	return ds_convenio_w;
elsif (ie_opcao_p = 'ST') then 
	return ie_status_w;
elsif (ie_opcao_p = 'DT') then 
	return dt_mesano_referencia_w;	
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
