CREATE OR REPLACE FUNCTION obter_dados_requisicao ( nr_requisicao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	L = Local de estoque
	R = Pessoa Requisitante
	C = Centro de Custo
	O = Operação de estoque
*/
cd_local_estoque_w		smallint;
cd_pessoa_requisitante_w		varchar(10);
ds_retorno_w			varchar(255);
cd_centro_custo_w		integer;
cd_operacao_estoque_w		requisicao_material.cd_operacao_estoque%type;


BEGIN

select	a.cd_local_estoque,
	a.cd_pessoa_requisitante,
	a.cd_centro_custo,
	a.cd_operacao_estoque
into STRICT	cd_local_estoque_w,
	cd_pessoa_requisitante_w,
	cd_centro_custo_w,
	cd_operacao_estoque_w
from	requisicao_material a
where	a.nr_requisicao = nr_requisicao_p;

if (ie_opcao_p = 'L') then
	ds_retorno_w := cd_local_estoque_w;
elsif (ie_opcao_p = 'R') then
	ds_retorno_w := cd_pessoa_requisitante_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w := cd_centro_custo_w;
elsif (ie_opcao_p = 'O') then
	ds_retorno_w := cd_operacao_estoque_w;
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
