CREATE OR REPLACE FUNCTION obter_dados_requisito_alt ( nr_seq_requisito_p bigint, ie_opcao_p bigint ) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao
	1 = requisito
	2 = pontos
*/
ds_alteracao_w		varchar(255);


BEGIN
if (ie_opcao_p	= 1) then
	select	ds_requisito
	into STRICT	ds_alteracao_w
	from	requisito_alteracao
	where	nr_sequencia	= nr_seq_requisito_p;
elsif (ie_opcao_p	= 2) then
	select	qt_pontos
	into STRICT	ds_alteracao_w
	from	requisito_alteracao
	where	nr_sequencia	= nr_seq_requisito_p;
end if;

return	ds_alteracao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
