CREATE OR REPLACE FUNCTION obter_dados_reserva_leito (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ie_reserva_leito_w  varchar(3);
ds_reserva_leito_w  varchar(255);

BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	max(ie_reserva_leito)
	into STRICT	ie_reserva_leito_w
	from	agenda_paciente
	where	nr_sequencia = nr_sequencia_p;
end if;

if (ie_reserva_leito_w IS NOT NULL AND ie_reserva_leito_w::text <> '')and (ie_opcao_p = 'D') then
	select	max(obter_valor_dominio(1545,ie_reserva_leito_w))
	into STRICT	ds_reserva_leito_w
	;
elsif (ie_opcao_p = 'C') then
	ds_reserva_leito_w := ie_reserva_leito_w;
end if;

return	ds_reserva_leito_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
