CREATE OR REPLACE FUNCTION obter_dados_residen_gest_popul ( cd_domicilio_p text, ie_opcao_p text, ie_tipo_domicilio_p text default null, ie_tratamento_agua_p text default null, ie_tipo_agua_p text default null, ie_destino_lixo_p text default null, ie_esgoto_sanitario_p text default null, ie_tipo_comunicacao_p text default null, ie_tipo_doenca_p text default null, ie_tipo_transporte_p text default null, ie_sem_energia_eletrica_p text default null) RETURNS varchar AS $body$
DECLARE


/*Descrição 				ie_opcao_p
Residencia					R
*/
ds_retorno_w				varchar(255) := 'S';
ie_energia_eletrica_w		varchar(1);


BEGIN

if (cd_domicilio_p IS NOT NULL AND cd_domicilio_p::text <> '') then

	if ( ie_opcao_p = 'R') then

		if (ie_sem_energia_eletrica_p = 'S') then

			ie_energia_eletrica_w := 'N';

		end if;

		Select 	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	domicilio_familia
		where   cd_domicilio = cd_domicilio_p
		and		coalesce(ie_tipo_domicilio,'N') = coalesce(ie_tipo_domicilio_p,coalesce(ie_tipo_domicilio,'N'))
		and		coalesce(ie_tratamento_agua,'N') = coalesce(ie_tratamento_agua_p,coalesce(ie_tratamento_agua,'N'))
		and		coalesce(ie_tipo_agua,'N') = coalesce(ie_tipo_agua_p,coalesce(ie_tipo_agua,'N'))
		and		coalesce(ie_destino_lixo,'N') = coalesce(ie_destino_lixo_p,coalesce(ie_destino_lixo,'N'))
		and		coalesce(ie_esgoto_sanitario,'N') = coalesce(ie_esgoto_sanitario_p,coalesce(ie_esgoto_sanitario,'N'))
		and		coalesce(ie_tipo_comunicacao,'N') = coalesce(ie_tipo_comunicacao_p,coalesce(ie_tipo_comunicacao,'N'))
		and		coalesce(ie_tipo_doenca,'N') = coalesce(ie_tipo_doenca_p,coalesce(ie_tipo_doenca,'N'))
		and		coalesce(ie_tipo_transporte,'N') = coalesce(ie_tipo_transporte_p,coalesce(ie_tipo_transporte,'N'))
		and		coalesce(ie_energia_eletrica,'S') = coalesce(ie_energia_eletrica_w,coalesce(ie_energia_eletrica,'S'));

	end if;

end if;


return ds_retorno_w;

end;
$body$
