SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_retorno_conv (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
dt_retorno_w		varchar(60);
ds_convenio_w		varchar(255);
ds_convenio_ret_w	varchar(255);
dt_retorno_conv_w	varchar(60);

/*ie_opcao_p 
	C - Descrição convênio atend 
	D - Data retorno atend 
	 
Usado na função protocolo documento, em uma visão passa o atendimento e na outra passa a sequencia da tab convenio_retono 
 
	CD - Desc Conv retorno 
	DT - Dt retorno Conv 
*/
 

BEGIN 
 
if (coalesce(nr_atendimento_p,0) <> 0) then 
	begin 
	if (ie_opcao_p = 'C') or (ie_opcao_p = 'D') then 
		 
		begin 
 
		select	max(a.dt_retorno), 
			max(substr(obter_nome_convenio(a.cd_convenio),1,150)) 
		into STRICT	dt_retorno_w, 
			ds_convenio_w 
		from 	convenio_retorno a, 
			convenio_retorno_item b, 
			conta_paciente c 
		where	c.nr_interno_conta	= b.nr_interno_conta 
		and	b.nr_seq_retorno	= a.nr_sequencia 
		and	c.nr_atendimento	= nr_atendimento_p;
		exception 
		when others then 
			dt_retorno_w := null;
			ds_convenio_w := null;
		end;
	elsif (ie_opcao_p = 'CD') or (ie_opcao_p = 'DT') then 
 
		begin 
		select	max(substr(obter_nome_convenio(a.cd_convenio),1,150)), 
			max(to_char(a.dt_retorno,'dd/mm/yyyy')) 
		into STRICT	ds_convenio_ret_w, 
			dt_retorno_conv_w 
		from 	convenio_retorno a 
		where 	a.nr_sequencia = nr_atendimento_p;
		exception 
		when others then 
			dt_retorno_conv_w := null;
			ds_convenio_ret_w := null;
		end;
 
	end if;
 
	end;
end if;
 
if (ie_opcao_p = 'C') then 
	ds_retorno_w := ds_convenio_w;
elsif (ie_opcao_p = 'D') then 
	ds_retorno_w := dt_retorno_w;
elsif (ie_opcao_p = 'CD') then 
	ds_retorno_w := ds_convenio_ret_w;
elsif (ie_opcao_p = 'DT') then 
	ds_retorno_w := dt_retorno_conv_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
