SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_ret_convenio_item (nr_seq_retorno_item_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p:
1 -
2 - numero do retorno
3 - cd_autorizacao
*/
ds_retorno_w		varchar(100);
nr_interno_conta_w	bigint;
nr_retorno_w		bigint;
cd_autorizacao_w	varchar(20);


BEGIN

if (nr_seq_retorno_item_p IS NOT NULL AND nr_seq_retorno_item_p::text <> '') then


	if (ie_opcao_p = 1) then

		select	b.nr_interno_conta
		into STRICT	nr_interno_conta_w
		from	convenio_retorno a,
			convenio_retorno_item b
		where	a.nr_sequencia	= b.nr_seq_retorno
		and	b.nr_sequencia	= nr_seq_retorno_item_p;

		ds_retorno_w	:= nr_interno_conta_w;

	elsif (ie_opcao_p = 2) then

		select	a.nr_seq_retorno
		into STRICT	nr_retorno_w
	        	from    convenio_retorno_item a
        		where   a.nr_sequencia	= nr_seq_retorno_item_p;

		ds_retorno_w	:= nr_retorno_w;

	elsif (ie_opcao_p = 3) then

		select	max(a.cd_autorizacao)
		into STRICT	cd_autorizacao_w
		from	convenio_retorno_item a
		where	a.nr_sequencia	= nr_seq_retorno_item_p;

		ds_retorno_w	:= cd_autorizacao_w;
	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
