SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_ret_convenio (nr_sequencia_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*
0 - Dt recebimento
1 - NÂº Conta
2 - Dt fechamento retorno
3 - Notas Fiscais do retorno
4 - Notas complementares
5 - Dt retorno
6 - Status Retorno
7 - NFe's do retorno
*/
ds_retorno_w		varchar(255);
dt_baixa_cr_w		timestamp;
dt_glosa_w		timestamp;
nr_interno_conta_w	bigint;
dt_fechamento_w	timestamp;
ds_nota_fiscal_w	varchar(255);
nr_nota_fiscal_w	varchar(255);
dt_retorno_p		timestamp;
ie_status_retorno_w	varchar(1);
nr_nfe_imp_w		nota_fiscal.nr_nfe_imp%type;

c01 CURSOR FOR
SELECT	b.nr_nota_fiscal
from	nota_fiscal b,
	convenio_retorno_nf a
where	a.nr_seq_retorno		= nr_sequencia_p
and	b.nr_sequencia		= a.nr_seq_nota_fiscal
and	b.ie_situacao 		= '1'

union

select	b.nr_nota_fiscal
from	nota_fiscal b,
	convenio_retorno a
where	b.nr_seq_protocolo		= a.nr_seq_protocolo
and	a.nr_sequencia		= nr_sequencia_p
and	b.ie_situacao 		= '1'

union

select	a.nr_nota_fiscal
from	nota_fiscal a,
	convenio_retorno b
where	a.ie_situacao = '1'
and	a.nr_seq_protocolo 	= b.nr_seq_protocolo
and	b.nr_sequencia		= nr_sequencia_p

union

select	b.nr_nota_fiscal
from	titulo_receber b,
	convenio_retorno a
where	a.nr_sequencia		= nr_sequencia_p
and	a.nr_seq_protocolo		= b.nr_seq_protocolo
and  	coalesce(b.nr_nota_fiscal,'0')	<> '0'

union

select	b.nr_nota_fiscal
from	titulo_receber b,
	convenio_retorno a,
	protocolo_convenio c
where	a.nr_sequencia		= nr_sequencia_p
and	c.nr_seq_lote_protocolo	= b.nr_seq_lote_prot
and 	a.nr_seq_protocolo 	= c.nr_seq_protocolo
and  	coalesce(b.nr_nota_fiscal,'0')	<> '0'

union

select	b.nr_nota_fiscal
from	nota_fiscal b,
	convenio_retorno a,
	protocolo_convenio c
where	a.nr_sequencia		= nr_sequencia_p
and	c.nr_seq_lote_protocolo	= b.nr_seq_lote_prot
and 	a.nr_seq_protocolo 	= c.nr_seq_protocolo
and  	coalesce(b.nr_nota_fiscal,'0')	<> '0'
order by 1;

c02 CURSOR FOR
SELECT	b.nr_nota_fiscal
from	nota_fiscal b,
	convenio_retorno_nf a
where	a.nr_seq_retorno	= nr_sequencia_p
and	b.nr_sequencia		= a.nr_seq_nota_fiscal;

c03 CURSOR FOR
SELECT	b.nr_nfe_imp
from	nota_fiscal b,
	convenio_retorno_nf a
where	a.nr_seq_retorno	= nr_sequencia_p
and	b.nr_sequencia		= a.nr_seq_nota_fiscal
and	b.ie_situacao 		= '1'

union

select	b.nr_nfe_imp
from	nota_fiscal b,
	convenio_retorno a
where	b.nr_seq_protocolo	= a.nr_seq_protocolo
and	a.nr_sequencia		= nr_sequencia_p
and	b.ie_situacao 		= '1'
order by 1;


BEGIN

if (ie_opcao_p = 0) then
	begin
	select	min(r.dt_baixa_cr)
	into STRICT	dt_baixa_cr_w
	from	convenio_retorno r,
		convenio_retorno_item i
	where	i.nr_interno_conta	= nr_sequencia_p
	and	r.nr_sequencia		= i.nr_seq_retorno
	and	r.ie_status_retorno	= 'F'
	and	i.vl_pago 		> 0;
	exception
	when others then
		dt_baixa_cr_w	:= null;
	end;
	ds_retorno_w		:= dt_baixa_cr_w;

elsif (ie_opcao_p = 1) then

	select	nr_interno_conta
	into STRICT	nr_interno_conta_w
	from	convenio_retorno_item
	where	nr_sequencia		= nr_sequencia_p;

	ds_retorno_w	:= to_char(nr_interno_conta_w);

elsif (ie_opcao_p = 2) then

	select	b.dt_fechamento
	into STRICT	dt_fechamento_w
	from	convenio_retorno b,
		convenio_retorno_item a
	where	a.nr_seq_retorno	= b.nr_sequencia
	and	a.nr_sequencia	= nr_sequencia_p;

	ds_retorno_w			:= dt_fechamento_w;

elsif (ie_opcao_p = 3) then

	ds_nota_fiscal_w			:= '';
	open c01;
	loop
	fetch c01 into
		nr_nota_fiscal_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		ds_nota_fiscal_w	:=  substr(ds_nota_fiscal_w || nr_nota_fiscal_w || ',',1,255);
	end loop;
	close c01;
	ds_retorno_w			:= ds_nota_fiscal_w;

elsif (ie_opcao_p = 4) then

	ds_nota_fiscal_w			:= '';
	open c02;
	loop
	fetch c02 into
		nr_nota_fiscal_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		ds_nota_fiscal_w	:=  substr(ds_nota_fiscal_w || nr_nota_fiscal_w || ',',1,255);
	end loop;
	close c02;
	ds_retorno_w			:= ds_nota_fiscal_w;

elsif (ie_opcao_p = 5) then
	select	max(a.dt_retorno)
	into STRICT	dt_retorno_p
	from	convenio_retorno a,
		convenio_retorno_item b
	where	a.nr_sequencia	= b.nr_seq_retorno
	and	b.nr_sequencia	= nr_sequencia_p;

	ds_retorno_w	:= dt_retorno_p;

elsif (ie_opcao_p = 6) then
	select	max(a.ie_status_retorno)
	into STRICT	ie_status_retorno_w
	from	convenio_retorno a,
		convenio_retorno_item b
	where	b.nr_seq_retorno	= a.nr_sequencia
	and	b.nr_sequencia		= nr_sequencia_p;

	ds_retorno_w	:= ie_status_retorno_w;

elsif (ie_opcao_p = 7) then

	ds_nota_fiscal_w			:= '';
	open c03;
	loop
	fetch c03 into
		nr_nfe_imp_w;
	EXIT WHEN NOT FOUND; /* apply on c03 */
		if (nr_nfe_imp_w IS NOT NULL AND nr_nfe_imp_w::text <> '') then
			ds_nota_fiscal_w	:=  substr(ds_nota_fiscal_w || nr_nfe_imp_w || ',',1,255);
		end if;
	end loop;
	close c03;
	ds_retorno_w			:= ds_nota_fiscal_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_ret_convenio (nr_sequencia_p bigint, ie_opcao_p bigint) FROM PUBLIC;

