SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_ret_tit (nr_titulo_p bigint, nr_protocolo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* 
IE_OPCAO_P 
 DTL = Data da Liberção do recebimento de convênio. 
*/
 
 
dt_liberacao_w		timestamp;
vl_retorno_w		varchar(255);
vl_recebido_w		double precision;
vl_glosa_w		double precision;
			   			

BEGIN 
 
if ( ie_opcao_p = 'DTL' ) and (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') and (nr_protocolo_p IS NOT NULL AND nr_protocolo_p::text <> '')  then 
  
	select	distinct max(h.dt_liberacao) 
	into STRICT	dt_liberacao_w 
	FROM protocolo_convenio b, titulo_receber a
LEFT OUTER JOIN titulo_receber_liq e ON (a.nr_titulo = e.nr_titulo)
LEFT OUTER JOIN convenio_retorno f ON (e.nr_seq_retorno = f.nr_sequencia)
LEFT OUTER JOIN convenio_ret_receb g ON (f.nr_sequencia = g.nr_seq_retorno)
LEFT OUTER JOIN convenio_receb h ON (g.nr_seq_receb = h.nr_sequencia)
WHERE a.nr_titulo    = nr_titulo_p and a.nr_seq_protocolo = nr_protocolo_p and a.nr_seq_protocolo = b.nr_seq_protocolo;
	 
	vl_retorno_w := to_char(dt_liberacao_w,'dd/mm/yyyy');
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_ret_tit (nr_titulo_p bigint, nr_protocolo_p bigint, ie_opcao_p text) FROM PUBLIC;

