SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_risco_apae ( nr_sequencia_p bigint, ie_opcao text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao
R	=	Risco
SR	=	Sequencia do Risco
I	=	Item
CI	=	Cor do Item*/
ds_risco_w		varchar(255);
ds_seq_apres_w	varchar(255);
ds_item_w		varchar(255);
ds_cor_w		varchar(255);


BEGIN
if (ie_opcao in ('R','SR')) then
	select	ds_risco,
			nr_seq_apresentacao
	into STRICT	ds_risco_w,
			ds_seq_apres_w
	from	risco_apae
	where	nr_sequencia = nr_sequencia_p;
elsif (ie_opcao in ('I','CI')) then
	select	ds_item,
			ds_cor_resultado
	into STRICT	ds_item_w,
			ds_cor_w
	from	risco_item_apae
	where	nr_sequencia = nr_sequencia_p;
end if;

if (ie_opcao = 'R') then
	return	ds_risco_w;
elsif (ie_opcao = 'SR') then
	return	ds_seq_apres_w;
elsif (ie_opcao = 'I') then
	return	ds_item_w;
elsif (ie_opcao = 'CI') then
	return	ds_cor_w;
else
	return	'';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_risco_apae ( nr_sequencia_p bigint, ie_opcao text) FROM PUBLIC;

