CREATE OR REPLACE FUNCTION obter_dados_rotina_prescr ( nr_seq_rotina_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_protocolo_w		bigint;
nr_seq_protocolo_w	bigint;
nr_seq_material_w	bigint;
cd_unidade_medida_w	varchar(255);
ds_retorno_w		varchar(255);
cd_material_w		bigint;


BEGIN

if (nr_seq_rotina_p IS NOT NULL AND nr_seq_rotina_p::text <> '') then

	select	cd_protocolo,
			nr_seq_prot,
			nr_seq_material
	into STRICT	cd_protocolo_w,
			nr_seq_protocolo_w,
			nr_seq_material_w
	from	rep_rotina_item
	where	nr_sequencia = nr_seq_rotina_p;

	if (ie_opcao_p = 'D') then

		select	max(cd_unidade_medida)
		into STRICT	ds_retorno_w
		from	protocolo_medic_material
		where	nr_seq_material = nr_seq_material_w
		and		nr_sequencia = nr_seq_protocolo_w
		and		cd_protocolo = cd_protocolo_w;

	elsif (ie_opcao_p = 'M') then

		select	max(cd_material)
		into STRICT	ds_retorno_w
		from	protocolo_medic_material
		where	nr_seq_material = nr_seq_material_w
		and		nr_sequencia = nr_seq_protocolo_w
		and		cd_protocolo = cd_protocolo_w;

	elsif (ie_opcao_p = 'I') then

		select	max(cd_intervalo)
		into STRICT	ds_retorno_w
		from	protocolo_medic_material
		where	nr_seq_material = nr_seq_material_w
		and		nr_sequencia = nr_seq_protocolo_w
		and		cd_protocolo = cd_protocolo_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
