SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_same_copia_pront ( nr_seq_solic_p bigint, ie_tipo_copia_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_seq_copia_prontuario_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_copia_prontuario_w
from	same_copia_prontuario
where	nr_seq_solic		= nr_seq_solic_p;


if (ie_tipo_copia_p = 'C') then

	select	cd_pessoa_fisica
	into STRICT	ds_retorno_w
	from	same_copia_prontuario
	where	nr_sequencia	= nr_seq_copia_prontuario_w;

elsif (ie_tipo_copia_p = 'D') then

	select	to_char(dt_entrega_pront, 'dd/mm/yyyy hh24:mi:ss')
	into STRICT	ds_retorno_w
	from	same_copia_prontuario
	where	nr_sequencia	= nr_seq_copia_prontuario_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
