SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_score_flex_queda ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	IE_OPCAO_P
DR = Primeiro registro
DA = Ultimo registro antes da data dt_evento_w
*/
dt_registro_w			varchar(50);
ds_resultado_w			varchar(255);
ds_retorno_w			varchar(255);
dt_evento_w				timestamp;
nr_atendimento_w		bigint;
qt_ponto_w				varchar(4);
nr_seq_score_flex_w		bigint;
nr_seq_tipo_evento_w	bigint;


BEGIN

select  a.dt_evento,
	a.nr_atendimento,
	d.nr_sequencia
into STRICT	dt_evento_w,
	nr_atendimento_w,
	nr_seq_tipo_evento_w
from	qua_evento_paciente a,
	queda_ficha_ocorrencia b,
	qua_evento c,
	qua_tipo_evento d
where	b.nr_seq_evento = a.nr_sequencia
and	c.nr_sequencia = a.nr_seq_evento
and	c.nr_seq_tipo = d.nr_sequencia
and	b.nr_sequencia = nr_sequencia_p;

select 	max(nr_sequencia)
into STRICT	nr_seq_score_flex_w
from	eif_escala
where	nr_seq_evento = nr_seq_tipo_evento_w
and	coalesce(ie_situacao,'A') = 'A';

If (nr_seq_score_flex_w IS NOT NULL AND nr_seq_score_flex_w::text <> '') then

	if (ie_opcao_p = 'DR') and (nr_atendimento_w > 0) then
		select	max(to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')),
			max(substr(obter_resultado_escala_eif(nr_sequencia,'D'),1,255)),
			max(somente_numero(obter_resultado_escala_eif(nr_sequencia,'T')))
		into STRICT	dt_registro_w,
			ds_resultado_w,
			qt_ponto_w
		from	escala_eif
		where	nr_sequencia = (SELECT	min(x.nr_sequencia)
					from	escala_eif x
					where	x.nr_atendimento = nr_atendimento_w
					and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
					and	coalesce(x.dt_inativacao::text, '') = '');

		ds_retorno_w := dt_registro_w || ' - ' ||qt_ponto_w ||obter_desc_expressao(296032)/*' pontos - '*/ ||' - '||ds_resultado_w;

	elsif (ie_opcao_p = 'DA') and (nr_atendimento_w > 0) and (dt_evento_w IS NOT NULL AND dt_evento_w::text <> '') then
		select	max(to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')),
			max(substr(obter_resultado_escala_eif(nr_sequencia,'D'),1,255)),
			max(somente_numero(obter_resultado_escala_eif(nr_sequencia,'T')))
		into STRICT	dt_registro_w,
			ds_resultado_w,
			qt_ponto_w
		from	escala_eif
		where	nr_seq_escala = nr_seq_score_flex_w
		and	nr_sequencia = (      SELECT	max(x.nr_sequencia)
						from	escala_eif x
						where	x.nr_atendimento = nr_atendimento_w
						and	x.nr_seq_escala = nr_seq_score_flex_w
						and	x.dt_avaliacao < dt_evento_w
						and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
						and	coalesce(x.dt_inativacao::text, '') = '');

		ds_retorno_w := dt_registro_w || ' - ' ||qt_ponto_w ||obter_desc_expressao(296032)/*' pontos - '*/||' - '||ds_resultado_w;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
