CREATE OR REPLACE FUNCTION obter_dados_seq_interno ( nr_seq_interno_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w            varchar(20);
cd_setor_atendimento_w  integer;
ie_status_unidade_w     varchar(3);


BEGIN

select  e.cd_setor_atendimento,
        e.ie_status_unidade
into STRICT    cd_setor_atendimento_w,
        ie_status_unidade_w
from    unidade_atendimento e
where   e.nr_seq_interno        = nr_seq_interno_p;

if (ie_opcao_p     = 'SA') then
        ds_retorno_w    := cd_setor_atendimento_w;
elsif (ie_opcao_p     = 'SU') then
        ds_retorno_w    := ie_status_unidade_w;
end if;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
