CREATE OR REPLACE FUNCTION obter_dados_session_servico (nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

qt_sessao_w	smallint;
nr_sessao_w	smallint;
ds_retorno_w	varchar(255) := '';
cd_pessoa_fisica_w pessoa_fisica.cd_pessoa_fisica%type;


BEGIN
	if (coalesce(cd_pessoa_fisica_p::text, '') = '') then
		select distinct(cd_pessoa_fisica)
		into STRICT cd_pessoa_fisica_w 
		from	agenda_consulta     
		where	nr_atendimento = nr_atendimento_p;
	end if;

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		select coalesce(qt_total_secao,0),
		coalesce(nr_secao,0)
		into STRICT	qt_sessao_w,
		nr_sessao_w
		from	agenda_consulta     
		where	nr_atendimento = nr_atendimento_p and cd_pessoa_fisica = coalesce(cd_pessoa_fisica_p,cd_pessoa_fisica_w)
    and nr_sequencia = (SELECT max(nr_sequencia) from agenda_consulta     
                        where	nr_atendimento = nr_atendimento_p and cd_pessoa_fisica = coalesce(cd_pessoa_fisica_p,cd_pessoa_fisica_w));

		if (qt_sessao_w IS NOT NULL AND qt_sessao_w::text <> '') and (nr_sessao_w IS NOT NULL AND nr_sessao_w::text <> '') then
			ds_retorno_w  := nr_sessao_w || ' / ' || qt_sessao_w;
		else
			ds_retorno_w  := nr_sessao_w;
		end if;
	end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
