CREATE OR REPLACE FUNCTION obter_dados_situacao_trib ( nr_seq_trib_prest_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

BEGIN
ds_retorno_w := '';

if (coalesce(nr_seq_trib_prest_p,'0') <> '0') then

	if (ie_opcao_p = 'CD') then
		begin
			select	max(cd_situacao)
			into STRICT	ds_retorno_w
			from	situacao_trib_prest_serv
			where	nr_sequencia= nr_seq_trib_prest_p;
		end;
	elsif (ie_opcao_p = 'DS') then
		begin
			select	max(ds_situacao)
			into STRICT	ds_retorno_w
			from	situacao_trib_prest_serv
			where	nr_sequencia= nr_seq_trib_prest_p;
		end;
	elsif (ie_opcao_p = 'OBS') then
		begin
			select	max(ds_observacao)
			into STRICT	ds_retorno_w
			from	situacao_trib_prest_serv
			where	nr_sequencia= nr_seq_trib_prest_p;
		end;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
