CREATE OR REPLACE FUNCTION obter_dados_socio_economico_pf ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* IE_OPCAO_P

CE	- Desc. classe econômica
CH1	- Condição habitação 1
CH2	- Condição habitação 2
CH3	- Condição habitação 3
ZP	- Zona de procedência
*/
ds_classe_econ_w	varchar(50);
ds_condicao_h1_w	varchar(50);
ds_condicao_h2_w	varchar(50);
ds_condicao_h3_w	varchar(50);
ds_zona_proced_w	varchar(50);

ds_retorno_w		varchar(255);


BEGIN

select	substr(obter_valor_dominio(1475,cd_classe_economica),1,50),
	substr(obter_valor_dominio(1476,cd_condicao_hab1),1,50),
	substr(obter_valor_dominio(1477,cd_condicao_hab2),1,50),
	substr(obter_valor_dominio(1478,cd_condicao_hab3),1,50),
	substr(obter_valor_dominio(1479,cd_zona_procedencia),1,50)
into STRICT	ds_classe_econ_w,
	ds_condicao_h1_w,
	ds_condicao_h2_w,
	ds_condicao_h3_w,
	ds_zona_proced_w
from	pessoa_perf_soc_economico
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;


if (ie_opcao_p	= 'CE') then
	ds_retorno_w	:= ds_classe_econ_w;
elsif (ie_opcao_p	= 'CH1') then
	ds_retorno_w	:= ds_condicao_h1_w;
elsif (ie_opcao_p	= 'CH2') then
	ds_retorno_w	:= ds_condicao_h2_w;
elsif (ie_opcao_p	= 'CH3') then
	ds_retorno_w	:= ds_condicao_h3_w;
elsif (ie_opcao_p	= 'ZP') then
	ds_retorno_w	:= ds_zona_proced_w;
end if;


return	ds_retorno_w;


End;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
